import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Admin.css';

const Admin = () => {
    const [addinfo, setAddInfo] = useState({
        name: '',
        author: '',
        price: '', 
        imgUrl: ''
    })
const handleChange = (e) => {
    console.log(e.target.name,e.target.value)
    const newUserInfo = {...addinfo};
    newUserInfo[e.target.name]
}

    return (
        <div className='admin-container'>
            <div className="shop-menu">
                <div className="inside-menu">
                <h5>Learn Something New</h5>
                <ul>
                    <Link to='/manageBook'><li>Manage book</li></Link>
                    <Link><li>Add Book</li></Link>
                    <Link><li>Edit book</li></Link>   
                </ul>
                </div>
            </div>
            <div className="addBook">
                    <form >
                    <div className="addBook-main">
                    <div className="input-side-one">
                        <span>Book name</span><br/>
                        <input onBlur={handleChange} name='name' type="text" placeholder='Enter name'/><br/><br/>
                        <span>Author name</span><br/>
                        <input onBlur={handleChange} name='author' type="text" placeholder='Enter author name'/>
                    </div>
                    <div className="input-side-two">
                        <span>Add price</span><br/>
                        <input onBlur={handleChange} name='price' type='number' placeholder='Enter price'/><br/><br/>
                        <span>Add Book Cover Photo</span><br/>
                        <input type="file" placeholder='Upload photo'/><br/><br/>
                        <input type="submit" value='save'/>
                    </div>
                    </div>
                </form> 
            </div>
        </div>
    );
};

export default Admin;
